local colWhite = Color(220, 220, 220, 230)
local colBG = Color(16, 16, 16, 90)
function SWEP:DrawHUD()
    if GetConVar("crosshair"):GetInt() ~= 1 then return end
	self:DrawCrosshairDot()
    
    local screenscale = BetterScreenScale()
    
    local wid, hei = 180 * screenscale, 64 * screenscale
	local x, y = ScrW() - wid - screenscale * 128, ScrH() - hei - screenscale * 72
	local spare = self:GetPrimaryAmmoCount()

	local yy = ScrH() - hei * 2 - screenscale * 84

    draw.RoundedBox(12, x, y * 0.99, wid, hei, colBG)
    if self:GetElementalState() == 0 then
		draw.SimpleTextBlurry("Pulse", "ZSHUDFont", x + wid * 0.5, yy * 1.11, colWhite, TEXT_ALIGN_CENTER, TEXT_ALIGN_CENTER)
	elseif self:GetElementalState() == 1 then
		draw.SimpleTextBlurry("Fire", "ZSHUDFont", x + wid * 0.5, yy * 1.11, colWhite, TEXT_ALIGN_CENTER, TEXT_ALIGN_CENTER)
	elseif self:GetElementalState() == 2 then
		draw.SimpleTextBlurry("Ice", "ZSHUDFont", x + wid * 0.5, yy * 1.11, colWhite, TEXT_ALIGN_CENTER, TEXT_ALIGN_CENTER)
    end
end